import React, { useReducer, useEffect, a } from 'react'

const initialState = {
	loading: true,
	error: '',
	post: {}
}

const reducer = (state, action) => {
	switch (action.type) {
		case 'SUCCESS':
			return {
				loading: false,
				error: '',
        post: {},
			}
		case 'ERROR':
			return {
				loading: false,
        error: 'ERROR!',
				post: {},
				
			}
		default:
			return state
	}
}

function UserAPICallReducer() {
	const [state, dispatch] = useReducer(reducer, initialState)

	useEffect(() => {
		axios
			.get(`https://jsonplaceholder.typicode.com/posts/1`)
			.then(response => {
				dispatch({ type: 'FETCH_SUCCESS', payload: response.data })
			})
			.catch(error => {
				dispatch({ type: 'FETCH_ERROR' })
			})
	}, [])
	return (
		<div>
			{state.loading ? 'Loading' : state.post.title}
			{state.error ? state.error : null}
		</div>
	)
}

export default UserAPICallReducer
