import React from 'react'

const initialState = {
	loading: true,
	error: '',
	post: {}
}

const reducer=(state,action)=>{
	switch (action.type) {
		case 'SUCCESS':
			return {
				loading: false,
				error: '',
                post: {},
			}
		case 'ERROR':
			return {
				loading: false,
                error: 'ERROR!',
				post: {},
				
			}
		default:
			return state
	}
}

export const UserApiCall=()=> {
	const [state, dispatch] = useReducer(reducer, initialState)

	useEffect(() => {
			 fetch(`https://jsonplaceholder.typicode.com/posts/1`)
			.then(response => response.json()) 
			.then(data=>{
				dispatch({ type: 'SUCCESS', result: data })
			})
			.catch(error => {
				dispatch({ type: 'ERROR' })
			})
	}, [])
	return (
		<div>
			<h2>Title: {state.post.title}</h2>
			<h2></h2>
			
			{state.error ? state.error : null}
		</div>
	)
}

export default UserApiCall
