import React from 'react'
import { useState, useEffect } from 'react';
// import User from '../component/Routess/pages/User';
// import ContextCreate from './ContexCreate';
// import Details from './Details';
export const UserApi = () => {
    
    const[user,setUser]=useState([])
    const getData=async()=>{
        const response=await fetch('https://jsonplaceholder.typicode.com/users');
        
        setUser(await response.json());
    }
    useEffect(()=>{
        getData();
    },[])
  const viewMore=(id)=>{
   
   user.map((value)=>{
    if(value.id===id)
    {
        document.write(`<div>
        <h1> name :${value.name}</h1> 
        <h3> username :${value.username}</h3> 
        <h3> email :${value.email}</h3> 
        <h3> addres :</h3>
        <h3> street:${value.address.street}</h3> 
        <h3> suite :${value.address.suite}</h3> 
        <h3> city :${value.address.city}</h3> 
        <h3> zipcode :${value.address.zipcode}</h3> 
        <h3> geo lat:${value.address.geo.lat}</h3> 
        <h3> geo lng :${value.address.geo.lng}</h3>       
        </div>`) 
    }

   }) 
  }
  return (
    <div>
        <div className='container'>
    <div className='row'>
            {
                user.map((CurrentData)=>{
                    return(
        <div className='col-md-4' key={CurrentData.id}>
            <div className="card cardd" >
            <p> Name: {CurrentData.name}</p>
            <div className="card-body">
            <h5 className="card-title">Email : {CurrentData.email}</h5>
            <p className="card-text">{CurrentData.address.street}</p>
            <button href="#" className="btn btn-primary" onClick={()=>viewMore(CurrentData.id)}>About</button>
            </div>
            </div>
        </div> 
                    )
                })
            }
    </div>
</div>

    
    </div>
  )
}
