import React from 'react'

const initialState = {
	loading: true,
	error: '',
	post: {}
}

const reducer=(state,action)=>{
	switch (action.type) {
		case 'SUCCESS':
			return {
				loading: false,
				error: '',
                post: {},
			}
		case 'ERROR':
			return {
				loading: false,
                error: 'ERROR!',
				post: {},
				
			}
		default:
			return state
	}
}

export const UserApiCall=()=> {
	const [state, dispatch] = useReducer(reducer, initialState)

	useEffect(() => {
			<div className="fetch"></div>(`https://jsonplaceholder.typicode.com/posts/1`)
			.then(response => {
				dispatch({ type: 'FETCH_SUCCESS', post: response.data })
			})
			.catch(error => {
				dispatch({ type: 'ERROR' })
			})
	}, [])
	return (
		<div>
			{state.loading ? 'Loading' : state.post.title}
			{state.error ? state.error : null}
		</div>
	)
}

export default UserApiCall
