import React, { useReducer, useEffect } from 'react'
import { type } from '@testing-library/user-event/dist/type'

const initialState = {
	loading: true,
	error: '',
	post:  action.result,
}

const reducer=(state,action)=>{
	switch (action.type) {
		case 'SUCCESS':
			return {
				loading: false,
				error: '',
                post: {},
			}
		case 'ERROR':
			return {
				loading: false,
                error: 'ERROR!',
				post: {},
				
			}
		default:
			return state
	}
}

export const UserApiCall=()=> {
	const [state, dispatch] = useReducer(reducer, initialState)

	useEffect(() => {
			 fetch('https://jsonplaceholder.typicode.com/posts/1')
			.then(response => response.json()) 
			.then(data=>{
				dispatch({ type: 'SUCCESS', result: data })
			})
			.catch(error =>{
				dispatch({ type: 'ERROR' })
			})
	}, [])

	return (

		<div>
			<h2>User Call API Reducer</h2>
			<h2>Title: {state.post.title}</h2>
			<h2>Body: {state.post.body}</h2>
		</div>
	)
}

export default UserApiCall
