import React, { useState, useReducer} from 'react'

const initialstate = [{ id: Date(), name: "", email: "" }]
const reducer = (state, action) => {

    switch (action.type) {
        case "add":
            return [...state, action.loading]
        case "delete":
            return state.filter((user) => {
            return user.id !== action.loading.id
            })
        default:
            return state
    }
}

export const UserApi = () => {
    const [state, dispatch] = useReducer(reducer, initialstate)
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")

    const AddContact = (e) => {
        e.preventDefault();
        const contact = { id: Date(), name: name, email: email }
        setName("")
        setEmail("")
        dispatch({ type: "add", loading: contact })
    }

    return (
        <div className="table-header bg-dark text-white">
        <div className='App' >
            <h1>Use Reducer Hooks</h1>
            <form onSubmit={AddContact}>
                <label>Name:
                    <input type="text" placeholder="name" value={name} onChange={(e) => setName(e.target.value)} />
                </label>
                <br />
                <label>Email:
                    <input type="text" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                </label>
                <div>
                    <button>ADD CONTACT</button>
                </div>
            </form>
            <div>

                {state.map(user => {
                    return (
                        <div key={user.id}>
                            <h1>{user.name}</h1>
                            <h2>{user.email}</h2>
                            <button onClick={() => dispatch({ type: "delete", loading: { id: user.id } })}>DELETE</button>
                        </div>
                
                    );
                })}

            </div>
        </div>
        </div>
    )
}
