import React, { useState } from 'react'

export const Register= () => {
    const [user, setUser] = useState({
        name: "",
        email: "",
        city: "",
        password: "",
       
    })
    const [error, setError] = useState({})

    const validation = () => {
        let error = {};
       
        if (!user.name) {
            error.name = "Name is Required";
        }

        if (!user.email) {
            error.email = "Email is Required";
        }

        if (!user.city) {
            error.city = "City is Required";
        }

        if (!user.password) {
            error.password = "Password is Required";
        }

        return error;

    };

    

    let name, email, city, password,  value;
    const postUserData = (event) => {
        name = event.target.name;
        email = event.target.email;
        city = event.target.city;
        password = event.target.password;
        value = event.target.value;

        
        

        if (name === "name") {
            if (value.length === 0) {
                setError({ ...error, name: "Name is Required" });
                setUser({
                    ...user,
                    name: "",
                });
            } else {
                setError({ ...error, name: "" });
                setUser({
                    ...user,
                    name: value,
                });
            }
        }   

        if (email === "email") {
            if (value.length === 0) {
                setError({ ...error, emai: "Name is Required" });
                setUser({
                    ...user,
                    name: "",
                });
            } else {
                setError({ ...error, name: "" });
                setUser({
                    ...user,
                    name: value,
                });
            }
        }   
        

        if (name === "name") {
            if (value.length === 0) {
                setError({ ...error, name: "Name is Required" });
                setUser({
                    ...user,
                    name: "",
                });
            } else {
                setError({ ...error, name: "" });
                setUser({
                    ...user,
                    name: value,
                });
            }
        }   
        
        
        if (city === "city") {
            if (value.length === 0) {
                setError({ ...error, city: "City is Required" });
                setUser({
                    ...user,
                    city: "",
                });
            } else {
                setError({ ...error, city: "" });
                setUser({
                    ...user,
                    city: value,
                });
            }
        }      
        
    };

    const SubmitInfo = (e) => {
        e.preventDefault();
        let ErrorList = validation();
        setError(validation());

        if (Object.keys(ErrorList).length === 0) {
            let reg = {
                name: user.name,
                       
            };
            console.log(reg);
        }
         
        if (Object.keys(ErrorList).length === 0) {
            let reg = {
                city: user.city,
                       
            };
            console.log(reg);
        }

        
    };


    return (
        <>
            <div className='container'>
                <h1>User Register</h1>
                <form onSubmit={SubmitInfo}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Name</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Enter name"
                            name="name"
                            value={user.name}
                            onChange={postUserData}
                        />
                        <span style={{ color: "red", marginLeft: "24px" }}>
                            {error.name}
                        </span>
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">City</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Enter city"
                            name="city"
                            value={user.city}
                            onChange={postUserData}
                        />
                        <span style={{ color: "red", marginLeft: "24px" }}>
                            {error.city}
                        </span>
                    </div>
                    

                    <button type="submit" className="btn btn-primary">Register</button>
                </form>
            </div>
        </>
    )
}
